mapper.select:
select id,title,sort,icon,create_time createTime,parent_id parentId,levels from category left join user_category_relation ur on c.id = ur.category_id where 1=1
and ur.user_id = #{userId}
and title like CONCAT('%',#{title},'%')

select c.id,c.title,c.sort,c.icon,c.create_time createTime,c.parent_id parentId,c.levels from category c left join user_category_relation ur on c.id = ur.category_id where 1=1
and ur.user_id = #{userId}
order by c.sort

mapper.update:
update category
parent_id=#{parentId}, sort=#{sort}, title=#{title}, icon=#{icon}, levels=#{levels}, update_time=now()
where id = #{id}

mapper.select:
SELECT c.id, c.title NAME, c.sort num, c.icon, c.create_time createTime, c.parent_id parentId, c.levels, 1 ismenu FROM category c LEFT JOIN user_category_relation ur on c.id = ur.category_id where 1=1
and ur.user_id = #{userId}
ORDER BY sort
